document.addEventListener("DOMContentLoaded",()=>{const t=document.querySelectorAll("h1, h2, img, div, article, main"),e=document.querySelector("main"),n=Array.from(t).filter(e=>!e.closest("nav, footer"));ScrollReveal().reveal(n,{opacity:0,duration:1e3,easing:"ease-in-out",reset:!1,afterReveal:function(t){t===e&&e.classList.remove("dn")}})})