document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector("main"),t=document.querySelectorAll("h1, h2, img, div, article"),n=Array.from(t).filter(e=>!e.closest("nav, footer"));ScrollReveal().reveal(n,{opacity:0,duration:1e3,easing:"ease-in-out",reset:!1,beforeReveal:()=>{e.classList.remove("dn")},afterReveal:()=>{e.style.visibility="visible"}}),new MutationObserver(()=>{e.classList.remove("dn")}).observe(e,{attributes:!0,attributeFilter:["class"]})})