document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector("main"),t=document.querySelector("footer");e&&e.classList.add("dn"),t&&t.classList.add("dn");const n=document.querySelectorAll("h1, h2, img, div, article, footer"),s=Array.from(n).filter(e=>!e.closest("nav")),o=ScrollReveal({opacity:0,duration:1e3,easing:"ease-in-out",reset:!1});o.reveal(s).then(()=>{e&&e.classList.remove("dn"),t&&t.classList.remove("dn")})})